import logo from './logoReis.png';
import './App.css';
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";

function App() {
    const [generalQueue, setGeneralQueue] = useState([]);
    const [preferentialQueue, setPreferentialQueue] = useState([]);
    const [currentPassword, setCurrentPassword] = useState("000");
    const [nextGeneralPassword, setNextGeneralPassword] = useState("G000");
    const [nextPreferentialPassword, setNextPreferentialPassword] = useState("P000");
    const [generalCallCount, setGeneralCallCount] = useState(0); // Contador de chamadas gerais
    const [preferentialCallCount, setPreferentialCallCount] = useState(0); // Contador de chamadas preferenciais

    const apiUrl = process.env.REACT_APP_API_URL;

    // Buscar as filas do backend
    const fetchQueues = useCallback(async () => {
        try {
            const response = await axios.get(`${apiUrl}/status`);
            const { generalQueue, preferentialQueue, currentPassword } = response.data;

            setGeneralQueue(generalQueue);
            setPreferentialQueue(preferentialQueue);
            setCurrentPassword(currentPassword);

            setNextGeneralPassword(generalQueue.length > 0 ? `G${generalQueue[0]}` : "G000");
            setNextPreferentialPassword(preferentialQueue.length > 0 ? `P${preferentialQueue[0]}` : "P000");
        } catch (error) {
            console.error("Erro ao buscar o estado das filas:", error);
        }
    }, [apiUrl]);

    const addPreferentialPassword = useCallback(async () => {
        try {
            await axios.post(`${apiUrl}/add-preferential`);
            fetchQueues();
        } catch (error) {
            console.error("Erro ao adicionar senha preferencial:", error);
        }
    }, [apiUrl, fetchQueues]);

    const addGeneralPassword = useCallback(async () => {
        try {
            await axios.post(`${apiUrl}/add-general`);
            fetchQueues();
        } catch (error) {
            console.error("Erro ao adicionar senha geral:", error);
        }
    }, [apiUrl, fetchQueues]);

    // Função para chamar senhas priorizando preferenciais e respeitando a ordem
    const callNextPassword = useCallback(async () => {
        try {
            await fetchQueues();

            // Se já foram chamadas 2 preferenciais, chama uma geral
            if (preferentialCallCount >= 2 && generalQueue.length > 0) {
                const generalPassword = generalQueue[0];
                const response = await axios.get(`${apiUrl}/next?password=${generalPassword}`);

                setCurrentPassword(response.data.password);
                setGeneralQueue((prev) => prev.slice(1)); // Remove senha chamada da fila geral
                setGeneralCallCount((prev) => prev + 1);
                setPreferentialCallCount(0); // Reseta o contador preferencial após chamar uma geral
            } 
            // Caso contrário, chama uma preferencial
            else if (preferentialQueue.length > 0) {
                const preferentialPassword = preferentialQueue[0];
                const response = await axios.get(`${apiUrl}/next?password=${preferentialPassword}`);

                setCurrentPassword(response.data.password);
                setPreferentialQueue((prev) => prev.slice(1)); // Remove senha chamada da fila preferencial
                setPreferentialCallCount((prev) => prev + 1);
            }
            // Se não houver preferenciais, chama uma geral
            else if (generalQueue.length > 0) {
                const generalPassword = generalQueue[0];
                const response = await axios.get(`${apiUrl}/next?password=${generalPassword}`);

                setCurrentPassword(response.data.password);
                setGeneralQueue((prev) => prev.slice(1)); // Remove senha chamada da fila geral
                setGeneralCallCount((prev) => prev + 1);
            }

            await fetchQueues(); // Atualiza as filas após chamar a senha
        } catch (error) {
            console.error("Erro ao chamar a próxima senha:", error);
        }
    }, [apiUrl, generalQueue, preferentialQueue, generalCallCount, preferentialCallCount, fetchQueues]);

    // Reset das filas
    const resetQueues = useCallback(async () => {
        try {
            await axios.post(`${apiUrl}/reset`);
            setGeneralCallCount(0);
            setPreferentialCallCount(0);
            fetchQueues();
        } catch (error) {
            console.error("Erro ao resetar as filas:", error);
        }
    }, [apiUrl, fetchQueues]);

    useEffect(() => {
        fetchQueues();
    }, [fetchQueues]);

    useEffect(() => {
        const handleKeyPress = (event) => {
            if (event.key === "p") {
                addPreferentialPassword();
            } else if (event.key === "g") {
                addGeneralPassword();
            } else if (event.key === "c") {
                callNextPassword();
            } else if (event.key === "r") {
                resetQueues();
            }
        };

        window.addEventListener("keydown", handleKeyPress);
        return () => {
            window.removeEventListener("keydown", handleKeyPress);
        };
    }, [addPreferentialPassword, addGeneralPassword, callNextPassword, resetQueues]);

    return (
        <main id="sidebar">
            <div id="container-senhas">
                <img src={logo} alt="Logo Reis" id='sidebar__logo' />
                <div id="container-current">
                    <div className="label">SUA VEZ!</div>
                    <div className="password" id="current-password">{currentPassword}</div>
                </div>
            </div>

            <div id="bottom-section">
                <h1>PRÓXIMAS SENHAS:</h1>
                <div className="bottom-containers" id="container-general">
                    <div className="bottom-label">GERAL -</div>
                    <div className="bottom-password" id="next-general">{nextGeneralPassword}</div>
                </div>

                <div className="bottom-containers" id="container-preferential">
                    <div className="bottom-label">PREFERENCIAL -</div>
                    <div className="bottom-password" id="next-preferential">{nextPreferentialPassword}</div>
                </div>
            </div>
        </main>
    );
}

export default App;
