import logo from './logoReis.png';
import './App.css';
import React, { useState, useEffect, useCallback } from "react";

function App() {
  const [generalQueue, setGeneralQueue] = useState([]);
  const [preferentialQueue, setPreferentialQueue] = useState([]);
  const [currentPassword, setCurrentPassword] = useState("000");

  // URL do back-end
  const apiUrl = "http://localhost:3001/api"; // Ajuste conforme necessário

  // Função para chamar a próxima senha, agora memorizada com useCallback
  const callNextPassword = useCallback(async () => {
      try {
          const response = await axios.get(`${apiUrl}/next`);
          const { password } = response.data;

          setCurrentPassword(password);
          updateQueues();
      } catch (error) {
          console.error("Erro ao chamar a próxima senha:", error);
      }
  }, [apiUrl]); // useCallback depende apenas da URL do back-end

  // Função para atualizar as filas
  const updateQueues = async () => {
      try {
          const response = await axios.get(`${apiUrl}/status`);
          const { generalQueue, preferentialQueue } = response.data;

          setGeneralQueue(generalQueue);
          setPreferentialQueue(preferentialQueue);
      } catch (error) {
          console.error("Erro ao atualizar as filas:", error);
      }
  };

  // UseEffect para buscar as filas inicialmente e sempre que chamar a próxima senha
  useEffect(() => {
      updateQueues();
  }, []); // Inicializa as filas quando o componente é montado

  // UseEffect para chamar a próxima senha ao pressionar a tecla
  useEffect(() => {
      const handleKeyPress = (event) => {
          if (event.key === "c") { // Tecla 'c' para chamar a próxima senha
              callNextPassword();
          }
      };

      window.addEventListener("keydown", handleKeyPress);
      return () => {
          window.removeEventListener("keydown", handleKeyPress);
      };
  }, [callNextPassword]); // Dependência da função callNextPassword memorizada

  return (
    <>
      <main id="sidebar">
        <div id="container-senhas">
          <img src={logo} alt="Logo Reis" id='sidebar__logo' />
          <div id="container-current">
            <div class="label">SUA VEZ!</div>
            <div class="password" id="current-password">000</div>
          </div>
        </div>

        <div id="bottom-section">
          <h1>PRÓXIMAS SENHAS:</h1>
          <div class="bottom-containers" id="container-general">
            <div class="bottom-label">GERAL -</div>
            <div class="bottom-password" id="next-general">G000</div>
          </div>

          <div class="bottom-containers" id="container-preferential">
            <div class="bottom-label">PREFERENCIAL -</div>
            <div class="bottom-password" id="next-preferential">P000</div>
          </div>
        </div>
      </main>
    </>
  );
}

export default App;
