import logo from './logoReis.png';
import './App.css';
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";

function App() {
    const [generalQueue, setGeneralQueue] = useState(["000"]);
    const [preferentialQueue, setPreferentialQueue] = useState(["000"]);
    const [currentPassword, setCurrentPassword] = useState("000");

    const apiUrl = process.env.REACT_APP_API_URL;

    // Buscar as filas do backend
    const fetchQueues = useCallback(async () => {
        try {
            const response = await axios.get(`${apiUrl}/status`);
            const { generalQueue, preferentialQueue, currentPassword } = response.data;

            setGeneralQueue(generalQueue);
            setPreferentialQueue(preferentialQueue);
            setCurrentPassword(currentPassword);
        } catch (error) {
            console.error("Erro ao buscar o estado das filas:", error);
        }
    }, [apiUrl]);

    //Adicionar senha a fila preferencial
    const addPreferentialPassword = useCallback(async () => {
        try {
            const response = await axios.post(`${apiUrl}/add-preferential`);

            if (response.status === 200) {
                console.log("Senha preferencial adicionada.");
                await fetchQueues(); // Atualiza a fila corretamente
            } else {
                console.error("Erro ao adicionar senha preferencial.");
            }
        } catch (error) {
            console.error("Erro ao adicionar senha preferencial:", error);
        }
    }, [apiUrl, fetchQueues]);

    //Adicionar senha a fila geral
    const addGeneralPassword = useCallback(async () => {
        try {
            const response = await axios.post(`${apiUrl}/add-general`);

            if (response.status === 200) {
                console.log("Senha geral adicionada.");
                await fetchQueues(); // Atualiza a fila corretamente
            } else {
                console.error("Erro ao adicionar senha geral.");
            }
        } catch (error) {
            console.error("Erro ao adicionar senha geral:", error);
        }
    }, [apiUrl, fetchQueues]);

    //Combinar e ordenar listas
    let passwsPreferentials = preferentialQueue.filter(item => item.startsWith("P"));
    let passwsGenerals = generalQueue.filter(item => item.startsWith("G"));

    let combinedPasswords = passwsPreferentials.concat(passwsGenerals);

    // Função para chamar a próxima senha respeitando a prioridade da fila preferencial
    const callNextPassword = useCallback(async () => {
        try {
            let nextPassword = null;

            // Verifica se há senhas na fila combinada
            if (combinedPasswords.length > 0) {
                nextPassword = combinedPasswords[0]; // Pega o primeiro item da fila combinada
                // Atualiza a fila combinada removendo o primeiro item
                setCombinedPasswords((prev) => prev.slice(1));
            }

            if (nextPassword) {
                const response = await axios.get(`${apiUrl}/next?password=${nextPassword}`);
                setCurrentPassword(response.data.password); // Chama a próxima senha
            }

            // Não precisa chamar fetchQueues aqui, pois a atualização já foi feita localmente
        } catch (error) {
            console.error("Erro ao chamar a próxima senha:", error);
        }
    }, [apiUrl, combinedPasswords]); // Alterado para usar a fila combinada



    // Reset das filas
    const resetQueues = useCallback(async () => {
        try {
            const response = await axios.post(`${apiUrl}/reset`);

            if (response.status === 200) { // Apenas limpa o frontend se o reset for bem-sucedido
                setGeneralQueue([]);
                setPreferentialQueue([]);
                setCurrentPassword("000"); // Indica que não há chamadas pendentes

                console.log("Filas resetadas com sucesso!");
            } else {
                console.error("Erro ao resetar no backend, resposta inesperada:", response);
            }

            await fetchQueues(); // Garante que o frontend tenha os dados atualizados do backend
        } catch (error) {
            console.error("Erro ao resetar as filas:", error);
        }
    }, [apiUrl, fetchQueues]);



    useEffect(() => {
        fetchQueues();
    }, [fetchQueues]);

    useEffect(() => {
        const handleKeyPress = (event) => {
            if (event.key === "p") {
                addPreferentialPassword(); // Apenas adiciona senha preferencial
            } else if (event.key === "g") {
                addGeneralPassword(); // Apenas adiciona senha geral
            } else if (event.key === "c") {
                callNextPassword(); // Apenas chama a próxima senha
            } else if (event.key === "r") {
                resetQueues(); // Reseta filas
            }
        };

        window.addEventListener("keydown", handleKeyPress);
        return () => {
            window.removeEventListener("keydown", handleKeyPress);
        };
    }, [addPreferentialPassword, addGeneralPassword, callNextPassword, resetQueues]);


    return (
        <main id="sidebar">
            <div id="container-senhas">
                <img src={logo} alt="Logo Reis" id='sidebar__logo' />
                <div id="container-current">
                    <div className="label">SUA VEZ!</div>
                    <div className="password" id="current-password">{currentPassword}</div>
                </div>
            </div>

            <div id="bottom-section">
                <h1>PRÓXIMAS SENHAS:</h1>
                <div className="bottom-containers" id="container-general">
                    <div className="bottom-label">GERAL -</div>
                    <div className="bottom-password">{generalQueue.length > 0 ? `G${generalQueue[0]}` : "G000"}</div>
                </div>

                <div className="bottom-containers" id="container-preferential">
                    <div className="bottom-label">PREFERENCIAL -</div>
                    <div className="bottom-password">{preferentialQueue.length > 0 ? `P${preferentialQueue[0]}` : "P000"}</div>
                </div>
            </div>
        </main>
    );
}

export default App;
